import { useState } from "react";
import CourseInput from "./components/CourseGoals/CourseInput/CourseInput";
import courseGoalList from "./components/CourseGoals/CourseGoalList/CourseGoalList";
import CourseGoalList from "./components/CourseGoals/CourseGoalList/CourseGoalList";
const App = () => {
  const [courseGoals, setCourseGoals] = useState([
    { text: "Do all exercises!", id: "g1" },
    { text: "Finish the course!", id: "g2" },
  ]);
  const addGoalHandler = (enteredValue) => {
    setCourseGoals((prevstate) => {
      const updatedGoals = [...prevstate];
      updatedGoals.unshift({
        text: enteredValue,
        id: Math.random().toString(),
      });
    });
  };
  const deleteItemHandler = (goalid) => {
    setCourseGoals((prevstate) => {
      const ubdateGoals = prevstate.filter((goal) => goal.id !== goalid);
      return ubdateGoals;
    });
  };
  let content = <p style={{ textAlign: "center" }}>No Goal Found</p>;
  if (courseGoals.length > 0) {
    content = (
      <courseGoalList
        items={courseGoals}
        onDeleteItem={deleteItemHandler}
      ></courseGoalList>
    );
  }
  return (
    <div>
      <section>
        <CourseInput onAddGoal={addGoalHandler}></CourseInput>
      </section>
      <section id="goals">{content}</section>
    </div>
  );
};
export default App;
